// 设置loading的logo图片base64数据：数据最好不要太大，以便能快速显示
var myLoaderImage = "data:image/jpeg;base64,";

// 自定义的loading视图界面（在samples基础上修改的）
var GameLoadingLayer = cc.Scene.extend({
    // 定义属性变量
    resourcesLength : 0,		// 标记资源的大小
    resourcesLoadCount : 0,		// 标记资源已经载入的大小
    labelProgress : null,		// 显示进度的百分比
	layerColorProgress: null,	// 进度条控件(LayerColor控件)
	layerColorProgressLength: null,		// 标记进度总长度
    
    // 实现初始化方法
    init : function(){
    	
    	// 调用父类的初始化方法
    	this._super();
    
    	// 使用self是个好习惯，避免函数参数中，this不生效的问题
        var self = this;
        
        // 获取当前窗体大小
		var size = cc.director.getWinSize();
		
        // 背景图片的默认大小，用于定位文字标签的位置
        var logoWidth = 160;
        var logoHeight = 200;

        // 添加背景颜色层
        var bgLayer = cc.LayerColor.create(cc.color(162, 186, 193, 255));
        bgLayer.setPosition(cc.visibleRect.bottomLeft);
        self.addChild(bgLayer, 0);

        // 添加logo图片：使用图片的base64数据
        var fontSize = 24, lblHeight =  -logoHeight / 2 + 100;
        if(myLoaderImage){
            // 设置背景图片：cc._loaderImage，在cocos2d-html5/cocos2d/core/scenes/CCSceneFile.js文件中定义，这里使用了自定义的变量
            cc.loader.loadImg(myLoaderImage, {isCrossOrigin : false }, function(err, img){
                logoWidth = img.width;
                logoHeight = img.height;
                self._initStage(img, cc.visibleRect.center);
            });
            fontSize = 14;
            lblHeight = -logoHeight / 2 - 10;	// 设置文本标签的位置
        }
        
        // 标题
        var labelTitle = cc.LabelTTF.create("水果特攻队", "Arial", fontSize*2);
        labelTitle.setPosition(cc.pAdd(cc.visibleRect.center, cc.p(0, -lblHeight)));
        labelTitle.setColor(cc.color(13, 96, 167));
        bgLayer.addChild(labelTitle, 10);
        
        // loading百分比
        var label = self.labelProgress = cc.LabelTTF.create("Loading... 0%", "Arial", fontSize);
        label.setPosition(cc.pAdd(cc.visibleRect.center, cc.p(0, lblHeight)));
        label.setColor(cc.color(13, 96, 167));
        bgLayer.addChild(self.labelProgress, 10);
        
        // 进度条
		self.layerColorProgressLength = 300;		// 限制进度条最大长度
		self.layerColorProgress = cc.LayerColor.create(cc.color(155, 100, 100, 128), 1, 15);	// 用颜色、宽、高来创建一个层
		self.layerColorProgress.setPosition(cc.pAdd(cc.visibleRect.center, cc.p(- self.layerColorProgressLength / 2, -logoHeight / 2 - 50)));
		// self.layerColorProgress.ignoreAnchorPointForPosition(false);
		// self.layerColorProgress.setAnchorPoint(cc.p(0, 0));
		bgLayer.addChild(self.layerColorProgress, 2);
        
        return true;	// 返回值根据需要，加上返回值是个好习惯
    },

    _initStage: function (img, centerPos) {
        var self = this;
        
        var texture2d = new cc.Texture2D();
        texture2d.initWithElement(img);
        texture2d.handleLoadedTexture();
        
        var logo = cc.Sprite.create(texture2d);
        logo.setScale(cc.contentScaleFactor());
        logo.x = centerPos.x;
        logo.y = centerPos.y;
        self.addChild(logo, 10);
    },

    onEnter: function () {
    	var self = this;
        cc.Node.prototype.onEnter.call(self);
        self.schedule(this._startLoading, 0.3);
    },

    onExit: function () {
        cc.Node.prototype.onExit.call(this);
        var tmpStr = "Loading... 0%";
        this.labelProgress.setString(tmpStr);
    },

    /**
     * init with resources
     * @param {Array} resources
     * @param {Function|String} cb
     */
    initWithResources: function (resources, cb) {
        if(typeof resources == "string") resources = [resources];
        this.resources = resources || [];
        this.cb = cb;
    },

    _startLoading: function () {
    	var self = this;
    	
        // 避免定时器被重复调用
        self.unschedule(self._startLoading);
        
        // 获取资源的大小
        var res = self.resources;
        self.resourcesLength = res.length;
        self.resourcesLoadCount = 0;
        cc.loader.load(res, function(result, count){ self.resourcesLoadCount = count; }, function(){
            if(self.cb)
                self.cb();
        });
        self.schedule(self._updatePercent);
    },

    _updatePercent: function () {
        var self = this;
        
        // 更新资源载入进度显示
        var count = self.resourcesLoadCount;
        var length = self.resourcesLength;
        var percent = (count / length * 100) | 0;
        percent = Math.min(percent, 100);
        self.labelProgress.setString("Loading... " + percent + "%");
		self.layerColorProgress.changeWidth(self.layerColorProgressLength * percent / 100);	// 设置进度条长度
		
		// 如果载入完毕，停止定时器
        if(count >= length) self.unschedule(self._updatePercent);
    }
});

// preload用来预加载loading界面，显示loading进度
GameLoadingLayer.preload = function(resources, cb){
    
    if(!cc.loaderScene) {
        cc.loaderScene = new GameLoadingLayer();
        cc.loaderScene.init();
    }
    cc.loaderScene.initWithResources(resources, cb);

    cc.director.runScene(cc.loaderScene);
    
    return cc.loaderScene;
};